import{_ as i,c as t,o as r,j as e,ae as s,a as o}from"./chunks/framework.BwJUVDD3.js";const l="/xiaozhi-android-client/assets/logo.DUF47VPs.png",g=JSON.parse('{"title":"xiaozhi-esp32-server","description":"基于ESP32的小智开源服务端，轻量级且高效的语音交互服务","frontmatter":{"title":"xiaozhi-esp32-server","description":"基于ESP32的小智开源服务端，轻量级且高效的语音交互服务"},"headers":[],"relativePath":"ecosystem/projects/xiaozhi-esp32-server/index.md","filePath":"ecosystem/projects/xiaozhi-esp32-server/index.md"}'),n={name:"ecosystem/projects/xiaozhi-esp32-server/index.md"};function h(d,a,c,p,v,m){return r(),t("div",null,a[0]||(a[0]=[e("h1",{id:"xiaozhi-esp32-server",tabindex:"-1"},[o("xiaozhi-esp32-server "),e("a",{class:"header-anchor",href:"#xiaozhi-esp32-server","aria-label":'Permalink to "xiaozhi-esp32-server"'},"​")],-1),e("div",{class:"project-header"},[e("div",{class:"project-logo"},[e("img",{src:l,alt:"xiaozhi-esp32-server Logo",onerror:"this.src='/py-xiaozhi/images/logo.png'; this.onerror=null;"})]),e("div",{class:"project-badges"},[e("span",{class:"badge platform"},"ESP32"),e("span",{class:"badge language"},"Python"),e("span",{class:"badge status"},"活跃开发中")])],-1),s('<div class="project-intro"><p>xiaozhi-esp32-server是为开源智能硬件项目<a href="https://github.com/78/xiaozhi-esp32" target="_blank">xiaozhi-esp32</a>提供的后端服务，根据<a href="https://ccnphfhqs21z.feishu.cn/wiki/M0XiwldO9iJwHikpXD5cEx71nKh" target="_blank">小智通信协议</a>使用Python实现，帮助您快速搭建小智服务器。</p></div><h2 id="适用人群" tabindex="-1">适用人群 <a class="header-anchor" href="#适用人群" aria-label="Permalink to &quot;适用人群&quot;">​</a></h2><p>本项目需要配合ESP32硬件设备使用。如果您已经购买了ESP32相关硬件，且成功对接过虾哥部署的后端服务，并希望独立搭建自己的<code>xiaozhi-esp32</code>后端服务，那么本项目非常适合您。</p><div class="warning-box"><h3>⚠️ 重要提示</h3><ol><li>本项目为开源软件，与对接的任何第三方API服务商（包括但不限于语音识别、大模型、语音合成等平台）均不存在商业合作关系，不为其服务质量及资金安全提供任何形式的担保。建议使用者优先选择持有相关业务牌照的服务商，并仔细阅读其服务协议及隐私政策。本软件不托管任何账户密钥、不参与资金流转、不承担充值资金损失风险。</li><li>本项目成立时间较短，还未通过网络安全测评，请勿在生产环境中使用。如果您在公网环境中部署学习本项目，请务必在配置文件<code>config.yaml</code>中开启防护。</li></ol></div><h2 id="核心特性" tabindex="-1">核心特性 <a class="header-anchor" href="#核心特性" aria-label="Permalink to &quot;核心特性&quot;">​</a></h2><div class="features-container"><div class="feature-item"><div class="feature-icon">🔄</div><h3>通信协议</h3><p>基于<code>xiaozhi-esp32</code>协议，通过WebSocket实现数据交互</p></div><div class="feature-item"><div class="feature-icon">💬</div><h3>对话交互</h3><p>支持唤醒对话、手动对话及实时打断，长时间无对话时自动休眠</p></div><div class="feature-item"><div class="feature-icon">🧠</div><h3>意图识别</h3><p>支持使用LLM意图识别、function call函数调用，减少硬编码意图判断</p></div><div class="feature-item"><div class="feature-icon">🌐</div><h3>多语言识别</h3><p>支持国语、粤语、英语、日语、韩语（默认使用FunASR）</p></div><div class="feature-item"><div class="feature-icon">🤖</div><h3>LLM模块</h3><p>支持灵活切换LLM模块，默认使用ChatGLMLLM，也可选用阿里百炼、DeepSeek、Ollama等</p></div><div class="feature-item"><div class="feature-icon">🔊</div><h3>TTS模块</h3><p>支持EdgeTTS（默认）、火山引擎豆包TTS等多种TTS接口，满足语音合成需求</p></div><div class="feature-item"><div class="feature-icon">📝</div><h3>记忆功能</h3><p>支持超长记忆、本地总结记忆、无记忆三种模式，满足不同场景需求</p></div><div class="feature-item"><div class="feature-icon">🏠</div><h3>IOT功能</h3><p>支持管理注册设备IOT功能，支持基于对话上下文语境下的智能物联网控制</p></div><div class="feature-item"><div class="feature-icon">🖥️</div><h3>智控台</h3><p>提供Web管理界面，支持智能体管理、用户管理、系统配置等功能</p></div></div><h2 id="部署方式" tabindex="-1">部署方式 <a class="header-anchor" href="#部署方式" aria-label="Permalink to &quot;部署方式&quot;">​</a></h2><p>本项目提供两种部署方式，请根据您的具体需求选择：</p><div class="deployment-table"><table><thead><tr><th>部署方式</th><th>特点</th><th>适用场景</th></tr></thead><tbody><tr><td><strong>最简化安装</strong></td><td>智能对话、IOT功能，数据存储在配置文件</td><td>低配置环境，无需数据库</td></tr><tr><td><strong>全模块安装</strong></td><td>智能对话、IOT、OTA、智控台，数据存储在数据库</td><td>完整功能体验</td></tr></tbody></table></div><p>详细部署文档请参考：</p><ul><li><a href="https://github.com/xinnan-tech/xiaozhi-esp32-server/blob/main/docs/Deployment.md" target="_blank" rel="noreferrer">Docker部署文档</a></li><li><a href="https://github.com/xinnan-tech/xiaozhi-esp32-server/blob/main/docs/Deployment_all.md" target="_blank" rel="noreferrer">源码部署文档</a></li></ul><h2 id="支持平台列表" tabindex="-1">支持平台列表 <a class="header-anchor" href="#支持平台列表" aria-label="Permalink to &quot;支持平台列表&quot;">​</a></h2><p>xiaozhi-esp32-server支持丰富的第三方平台和组件：</p><h3 id="llm-语言模型" tabindex="-1">LLM 语言模型 <a class="header-anchor" href="#llm-语言模型" aria-label="Permalink to &quot;LLM 语言模型&quot;">​</a></h3><div class="platform-item"><h4>接口调用</h4><p><strong>支持平台：</strong>阿里百炼、火山引擎豆包、深度求索、智谱ChatGLM、Gemini、Ollama、Dify、Fastgpt、Coze</p><p><strong>免费平台：</strong>智谱ChatGLM、Gemini</p><p><em>实际上，任何支持openai接口调用的LLM均可接入使用</em></p></div><h3 id="tts-语音合成" tabindex="-1">TTS 语音合成 <a class="header-anchor" href="#tts-语音合成" aria-label="Permalink to &quot;TTS 语音合成&quot;">​</a></h3><div class="platform-item"><h4>接口调用</h4><p><strong>支持平台：</strong>EdgeTTS、火山引擎豆包TTS、腾讯云、阿里云TTS、CosyVoiceSiliconflow、TTS302AI、CozeCnTTS、GizwitsTTS、ACGNTTS、OpenAITTS</p><p><strong>免费平台：</strong>EdgeTTS、CosyVoiceSiliconflow(部分)</p><h4>本地服务</h4><p><strong>支持平台：</strong>FishSpeech、GPT_SOVITS_V2、GPT_SOVITS_V3、MinimaxTTS</p><p><strong>免费平台：</strong>FishSpeech、GPT_SOVITS_V2、GPT_SOVITS_V3、MinimaxTTS</p></div><h3 id="asr-语音识别" tabindex="-1">ASR 语音识别 <a class="header-anchor" href="#asr-语音识别" aria-label="Permalink to &quot;ASR 语音识别&quot;">​</a></h3><div class="platform-item"><h4>接口调用</h4><p><strong>支持平台：</strong>DoubaoASR</p><h4>本地服务</h4><p><strong>支持平台：</strong>FunASR、SherpaASR</p><p><strong>免费平台：</strong>FunASR、SherpaASR</p></div><h3 id="更多组件" tabindex="-1">更多组件 <a class="header-anchor" href="#更多组件" aria-label="Permalink to &quot;更多组件&quot;">​</a></h3><ul><li><strong>VAD语音活动检测</strong>：支持SileroVAD（本地免费使用）</li><li><strong>记忆存储</strong>：支持mem0ai（1000次/月额度）、mem_local_short（本地总结，免费）</li><li><strong>意图识别</strong>：支持intent_llm（通过大模型识别意图）、function_call（通过大模型函数调用完成意图）</li></ul><h2 id="参与贡献" tabindex="-1">参与贡献 <a class="header-anchor" href="#参与贡献" aria-label="Permalink to &quot;参与贡献&quot;">​</a></h2><p>xiaozhi-esp32-server是一个活跃的开源项目，欢迎贡献代码或提交问题反馈：</p><ul><li><a href="https://github.com/xinnan-tech/xiaozhi-esp32-server" target="_blank" rel="noreferrer">GitHub仓库</a></li><li><a href="https://github.com/xinnan-tech/xiaozhi-esp32-server/issues" target="_blank" rel="noreferrer">问题反馈</a></li><li><a href="https://github.com/xinnan-tech/xiaozhi-esp32-server/blob/main/docs/contributor_open_letter.md" target="_blank" rel="noreferrer">致开发者的公开信</a></li></ul>',24)]))}const f=i(n,[["render",h]]);export{g as __pageData,f as default};
